import React, { useEffect } from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import { connect } from "react-redux";

import { auth } from "./firebase/firebase.utils";

import Header from "./pages/header/header.component";
import HomePage from "./pages/homepage/homepage.component";
import Login from "./pages/login/login.component";
import { setCurrentUser, listAllUser } from "./redux/user/user.actions";

import { createUserProfileDocument } from "./firebase/firebase.utils";

import { firestore } from "./firebase/firebase.utils";

import { setMessageChatted } from "./redux/messages/messages.action";
import "./App.css";

class App extends React.Component {
  unsubscribeFromAuth = null;

  componentDidMount() {
    const { setCurrentUser, listAllUser, currentUser, selectedUser } =
      this.props;

    this.unsubscribeFromAuth = auth.onAuthStateChanged(async (userAuth) => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);
        userRef.onSnapshot((snapShot) => {
          const db = firestore
            .collection("users")
            .onSnapshot((querySnapshot) => {
              var users = [];
              querySnapshot.forEach((doc) => {
                if (snapShot.id != doc.id)
                  users.push({ id: doc.id, ...doc.data() });
              });

              listAllUser(users);
            });

          setCurrentUser({
            id: snapShot.id,
            ...snapShot.data(),
          });

          firestore
            .collection("messages")
            // .where("user1", "in", [snapShot.id])
            // .orderBy("chattedAt")
            .onSnapshot((querySnapshot) => {
              var allMessages = [];
              querySnapshot.forEach((doc) => {
                // if (
                //   (doc.data().user1 == currentUser.id &&
                //     doc.data().user2 == user.id) ||
                //   (doc.data().user1 == user.id && doc.data().user2 == currentUser.id)
                // )
                {
                  allMessages.push(doc.data());
                }
              });

              setMessageChatted(allMessages);
            });
        });
      }
      setCurrentUser(userAuth);
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div className="app">
        <Header></Header>
        <Switch>
          <Route
            exact
            path="/"
            render={() =>
              !this.props.currentUser ? <Redirect to="/login" /> : <HomePage />
            }
          />
          <Route exact path="/login" component={Login} />
        </Switch>
      </div>
    );
  }
}

const mapStateToProps = ({ user }) => ({
  currentUser: user.currentUser,
  selectedUser: user.selectedUser,
});

const mapDispatchToProps = (dispatch) => ({
  setCurrentUser: (user) => dispatch(setCurrentUser(user)),
  listAllUser: (allUser) => dispatch(listAllUser(allUser)),
  setMessageChatted: (messageChatted) =>
    dispatch(setMessageChatted(messageChatted)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
