export function toMatcherArray(modifierFromProp) {
    if (Array.isArray(modifierFromProp)) {
        return modifierFromProp;
    }
    else if (modifierFromProp !== undefined) {
        return [modifierFromProp];
    }
    else {
        return [];
    }
}
