import React, { Component } from "react";
import { connect } from "react-redux";

import Avatar from "../avatar/avatar.component";

import { setSelectedUser } from "../../redux/user/user.actions";
import "./user-item.styles.scss";

const UserItem = ({ user, setSelectedUser }) => {
  const { name, isOnline } = user;
  const onSelectedUser = () => {
    setSelectedUser(user);
  };

  return (
    <div
      // style={{ animationDelay: `0.${this.props.animationDelay}s` }}
      // onClick={this.selectChat}

      onClick={onSelectedUser}
      className={`chatlist__item ${isOnline ? isOnline : ""} `}
    >
      <Avatar
        // image={
        //   this.props.image ? this.props.image : "http://placehold.it/80x80"
        // }
        isOnline={isOnline}
      />

      <div className="userMeta">
        <p>{name}</p>
        <span className="activeTime">32 mins ago</span>
      </div>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => ({
  setSelectedUser: (user) => dispatch(setSelectedUser(user)),
});

export default connect(null, mapDispatchToProps)(UserItem);
