import React from "react";

import FormInput from "../form-input/form-input.component";
import Button from "../../components/button/button";

import { auth, createUserProfileDocument } from "../../firebase/firebase.utils";

import "./sign-up.styles.scss";

const SignUp = ({ signUpStart }) => {
  const [userCredentials, setUserCredentials] = useState({
    userName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const { fullname, email, password, confirmPassword } = this.state;

  handleSubmit = async (event) => {
    event.preventDefault();

    const { displayName, email, password, confirmPassword } = this.state;

    if (password !== confirmPassword) {
      alert("passwords don't match");
      return;
    }

    try {
      const { user } = await auth.createUserWithEmailAndPassword(
        email,
        password
      );

      await createUserProfileDocument(user, { displayName });

      this.setState({
        displayName: "",
        email: "",
        password: "",
        confirmPassword: "",
      });
    } catch (error) {
      console.error(error);
    }
  };

  handleChange = (event) => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  return (
    <div className="sign-up">
      <div className="sign-up-describe">
        <h2 className="title">I do not have a account</h2>
      </div>
      <form className="sign-up-form" onSubmit={this.handleSubmit}>
        <div className="form-info">
          <div>
            <label for="userName">Full Name</label>
            <input
              id="userName"
              type="text"
              name="userName"
              value={userName}
              onChange={this.handleChange}
              label="Mark Phan"
              required
            />
          </div>

          <div>
            <label for="email">Email address</label>
            <input
              id="email"
              type="email"
              name="email"
              value={email}
              onChange={this.handleChange}
              label="Email"
              required
            />
          </div>

          <div>
            <label for="password">Password</label>
            <input
              id="password"
              type="password"
              name="password"
              value={password}
              onChange={this.handleChange}
              label="Password"
              required
            />
          </div>

          <div>
            <label for="confirmPassword">Confirm Password</label>
            <input
              id="confirmedPassword"
              type="password"
              name="confirmPassword"
              value={confirmPassword}
              onChange={this.handleChange}
              label="Confirm Password"
              required
            />
          </div>

          <div>
            <label for="confirmPassword">Birthday</label>
            <input
              id="confirmedPassword"
              type="password"
              name="confirmPassword"
              value={confirmPassword}
              onChange={this.handleChange}
              label="Confirm Password"
              required
            />
          </div>

          <div>
            <label for="confirmPassword">Gender</label>
            {/* <select onChange={this.genderhandler} defaultValue="Select Gender"> */}
            <select onChange={this.handleChange} defaultValue="Select Gender">
              <option defaultValue>Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          </div>
        </div>

        <Button type="submit">SIGN UP</Button>
      </form>
    </div>
  );
};

export default SignUp;
